import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import { login } from './services/userService';
import './login.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const LoginForm = () => {
  const [loginData, setLoginData] = useState({
    email:"",
    password:"",
  });

  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async(e) => {
    e.preventDefault();

    try {
      const response = await login(loginData.email, loginData.password);
      console.log(response); // This will log the response data from the backend
    
      // Handle success
      toast.success(response.data); // Assuming the response data contains a success message
    
    } catch (error) {
      console.error(error);
    
      if (error.response) {
        toast.error(error.response.data.message); // Display the server's error message
      } else {
        toast.error('An error occurred'); // Display a generic error message
      }
    }
  };

  return (
    <div class="login-form">
      <h2>SIGN IN</h2>
      <form onSubmit={handleSubmit}>
      <div class="field">
  <label>Email:</label>
  <input
    type="text"
    id="email"
    name="email"
    value={loginData.email}
    onChange={handleInputChange}
    class="password" /* Apply same class as for password input */
  />
</div>
<div class="field">
  <label>Password:</label>
  <input
    type="password"
    id="password"
    name="password"
    value={loginData.password}
    onChange={handleInputChange}
    class="password"
  />
</div>
        <button type="submit">SIGN IN</button>
        <p>Don't have an account &nbsp;&nbsp;<Link to= "/register"> Register</Link></p>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default LoginForm;
